#include "geometry/Tile.hpp"static char error_createPhase[] = "Cannot perform after create phase";void Tile::init(float height_arg, float width_arg){    if(mCreatePhase) logError(__FILE__, __LINE__, error_createPhase);        mHeight = height_arg;    mWidth = width_arg;}void Tile::create(GL4_Mesh* mesh, GL4_Vertex_Factory* factory){    mesh->add_feed(factory->);    mCreatePhase = true;}